import { useState, useEffect } from "react";
import styles from "./Formulario.module.css";
import Input from "./Input";
import SubmitButton from "./SubmitButton";

function Formulario(handleSubmit, btnText, projectData) {
  const [categories, setCategories] = useState([]);
  const [project, setProject] = useState(projectData || {});
  const [isDataSaved, setIsDataSaved] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/contatos", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const submit = (e) => {
    e.preventDefault();
    handleSubmit(project);
  };

  function handleChange(e) {
    setProject({ ...project, [e.target.name]: e.target.value });
    console.log(project)
  }

  fetch("http://localhost:5000/contatos", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(project),
  })
    .then((resp) => resp.json())
    .then((response) => {
      console.log("Dados enviados com sucesso:", response);
      setIsDataSaved(true);
      e.target.reset();
    })
    .catch((err) => console.log(err));
    
    return (
      <form className={styles.formulario_container} onSubmit={Submit}>
    {isDataSaved && <p>Agradeço o contato, assim que possível retornarei!</p>}
    <Input type="text" text="Nome" name="name" placeholder="Nome" handleOnChange={handleChange} />
    <Input type="text" text="Email" name="email" placeholder="Email" handleOnChange={handleChange} />
    <Input
      type="number"
      text="Telefone"
      name="telefone"
      placeholder="Telefone"
      handleOnChange={handleChange}
      />
    <Input
      type="textarea"
      text="Mensagem"
      name="mensagem"
      placeholder="Sua mensagem..."
      handleOnChange={handleChange}
      />
    <SubmitButton />
  </form>
);
}

export default Formulario;
