import { useState } from 'react'

const Banco = {
  createGet: () => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
      fetch("http://localhost:5000/contatos", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((resp) => resp.json())
        .then((data) => {
          setCategories(data);
        })
        .catch((err) => console.log(err));
    }, []);

    const submit = (e) => {
      e.preventDefault();
    };
  },

  createPost: (project) => {
    fetch("http://localhost:5000/contatos", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(project),
    })
      .then((resp) => resp.json())
      .then((data) => {
        console.log("Dados enviados com sucesso:", data);
        history.pushState('/informacoes', { message: 'Agradeço o contato!' });
        setIsDataSaved(true);
        //e.target.reset();
      })
      .catch((err) => console.log(err));
  },
};

export default Banco
