import { useState, useEffect } from "react";
import styles from "./Formulario.module.css";
import Input from "./Input";
import SubmitButton from "./SubmitButton";

function Formulario(handleSubmit, btnText, projectData) {
  const [categories, setCategories] = useState([]);
  const [project, setProject] = useState(projectData || {});
  const [isDataSaved, setIsDataSaved] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/contatos", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const submit = (e) => {
    e.preventDefault();
    handleSubmit(project);
  };

  function handleChange(e) {
    setProject({ ...project, [e.target.name]: e.target.value });
  }

  function handleCategory(e) {
    setProject({
      ...project,
      category: {
        id: e.target.value,
        name:e.target.options[e.target.selectedIndex].text,
      },
    })
  }
  function createPost(project){
 
    fetch("http://localhost:5000/contatos", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
    },
      body: JSON.stringify(project),
    })
      .then((resp) => resp.json())
      .then((data) => {
        console.log("Dados enviados com sucesso:", data);
      //setIsDataSaved(true);
      //e.target.reset();
    })
    .catch((err) => console.log(err));
  }
  return (
    <form onSubmit={submit} className={styles.formulario_container}>
      {isDataSaved && <p>Agradeço o contato, assim que possível retornarei!</p>}
      <Input
        type="text"
        text="Nome"
        name="name"
        placeholder="Nome"
        handleOnChange={handleChange}
        value={project.name ? project.name : ''}
      />
      <Input
        type="text"
        text="Email"
        name="email"
        placeholder="Email"
        handleOnChange={handleChange}
        value={project.email ? project.email : ''}
      />
      <Input
        type="number"
        text="Telefone"
        name="telefone"
        placeholder="Telefone"
        handleOnChange={handleChange}
        value={project.number}
      />
      <Input
        type="textarea"
        text="Mensagem"
        name="mensagem"
        placeholder="Sua mensagem..."
        handleOnChange={handleChange}
        value={project.mensagem ? project.mensagem : ''}
      />
      <SubmitButton text={btnText} />
    </form>
  );
}

export default Formulario;
