import { useState, useEffect } from "react";
import styles from "./Formulario.module.css";
import Input from "./Input";
import SubmitButton from "./SubmitButton";

function Formulario() {
  const [categories, setCategories] = useState([]);
  const [isDataSaved, setIsDataSaved] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/categories", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);

    const data = {
      name: formData.get("name"),
      email: formData.get("email"),
      telefone: formData.get("telefone"),
      mensagem: formData.get("mensagem"),
    };

    fetch("http://localhost:5000/contatos", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((resp) => resp.json())
      .then((response) => {
        console.log("Dados enviados com sucesso:", response);
        setIsDataSaved(true);
        event.target.reset();
      })
      .catch((err) => console.log(err));
  };

  return (
    <form className={styles.formulario_container} onSubmit={handleSubmit}>
      {isDataSaved && <p>Agradeço o contato, assim que possível retornarei!</p>}
      <Input type="text" text="Nome" name="name" placeholder="Nome" />
      <Input type="text" text="Email" name="email" placeholder="Email" />
      <Input type="number" text="Telefone" name="telefone" placeholder="Telefone" />
      <Input type="textarea" text="Mensagem" name="mensagem" placeholder="Sua mensagem..." />
      <SubmitButton />
    </form>
  );
}

export default Formulario;
