import { useState } from 'react';
import styles from './Formulario.module.css';
import Input from './Input';
import SubmitButton from './SubmitButton';

function Formulario({ handleSubmit, btnText, projectData }) {
  const [project, setProject] = useState(projectData || {});
  const [isDataSaved, setIsDataSaved] = useState(false);

  const handleChange = (e) => {
    setProject({ ...project, [e.target.name]: e.target.value });
  };

  const handleSubmitForm = (e) => {
    e.preventDefault();
    handleSubmit(project);
    // Aqui você pode fazer a chamada para a função de envio dos dados ao banco
    // ou executar qualquer outra ação necessária após o envio do formulário.
    setIsDataSaved(true);
  };

  return (
    <form onSubmit={handleSubmitForm} className={styles.formulario_container}>
      {isDataSaved && <p>Agradeço o contato, assim que possível retornarei!</p>}
      <Input
        type="text"
        text="Nome"
        name="name"
        placeholder="Nome"
        handleOnChange={handleChange}
        value={project.name || ''}
      />
      <Input
        type="text"
        text="Email"
        name="email"
        placeholder="Email"
        handleOnChange={handleChange}
        value={project.email || ''}
      />
      <Input
        type="number"
        text="Telefone"
        name="telefone"
        placeholder="Telefone"
        handleOnChange={handleChange}
        value={project.telefone || ''}
      />
      <Input
        type="textarea"
        text="Mensagem"
        name="mensagem"
        placeholder="Sua mensagem..."
        handleOnChange={handleChange}
        value={project.mensagem || ''}
      />
      <SubmitButton text={btnText} />
    </form>
  );
}

export default Formulario;
